import React, { useReducer } from "react";
import MealContext from "./MealContext";


const defaultCartState = {
    items : [],
    totalAmount : 0
}

const cartReducer = (state, action)=>{

    if(action.type === "ADD"){
        const newItems = [...state.items, action.item];
        const newTotalAmount = state.totalAmount + action.item.amount * action.item.price;

        return {newItems, newTotalAmount};
    }

    return defaultCartState;
}

const MealContextProvider = (props)=>{

    const [cartState, despatchCart] = useReducer(cartReducer, defaultCartState);

    // console.log(cartState);

    const addItemHandler = (item)=>{
        // despatchCart({type : "ADD", item : item});
        console.log(item);
    }

    const removeItemHandler = (id)=>{}

    return <MealContext.Provider value={{
        items : cartState.items,
        totalAmount : cartState.totalAmount,
        addItem : addItemHandler,
        removeItem : removeItemHandler
    }}>
        {props.children}
    </MealContext.Provider>
}

export default MealContextProvider;
